mapper.select:
SELECT branch_id AS branchId, branch_name AS branchName
FROM branch
LIMIT 5;

SELECT SUM(s.sales_quantity) FROM sales s JOIN item i ON s.item_id = i.item_id
AND branch_id = #{branchId}
GROUP BY i.category2 ORDER BY i.category2

SELECT SUM(ic.quantity) FROM incoming ic JOIN item i ON ic.item_id = i.item_id
AND branch_id = #{branchId}
GROUP BY i.category2 ORDER BY i.category2

WITH RECURSIVE CTE AS ( SELECT DATE(#{startDate}) AS date UNION ALL SELECT DATE_ADD(date, INTERVAL 1 DAY) FROM CTE WHERE date < DATE(#{endDate}) ) SELECT CTE.date AS dayOfWeek, IFNULL(s.sum, 0) AS sumOfSales FROM CTE LEFT OUTER JOIN ( SELECT DATE(sales_date) AS temp, SUM(sales_quantity) AS sum FROM sales WHERE sales_date BETWEEN DATE(#{startDate}) AND (DATE(#{endDate}) + 1)
AND branch_id = #{branchId}
GROUP BY DATE(sales_date) ORDER BY DATE(sales_date) ASC ) s ON CTE.date = temp

SELECT SUM(s.sales_quantity * price) AS totalSalesAmount
FROM sales s
JOIN item i
ON s.item_id = i.item_id
WHERE DATE_FORMAT(s.sales_date, "%Y-%m-%d") = #{date}

    AND branch_id = #{branchId}

SELECT SUM(s.sales_quantity * price) AS totalSalesAmount
FROM sales s
JOIN item i
ON s.item_id = i.item_id
WHERE DATE_FORMAT(s.sales_date, "%Y-%m") = #{date}

    AND branch_id = #{branchId}

SELECT SUM(s.sales_quantity * price) AS totalSalesAmount
FROM sales s
JOIN item i
ON s.item_id = i.item_id
WHERE DATE_FORMAT(s.sales_date, "%Y") = #{date}

    AND branch_id = #{branchId}
